// store.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    selectedActivities: [],
    // Add other state properties for other categories if needed
  },
  mutations: {
    updateSelectedActivities(state, selectedActivities) {
      state.selectedActivities = selectedActivities;
    },
    // Add other mutations for other categories if needed
  },
});
